import React from 'react';

import {Link} from 'react-router-dom';

export const listPhone = [
        {
            id: 0,
            name: 'iPhone 7',
            manufacturer: 'Apple',
            description: 'lorem ipsum dolor sit amet consectetur.',
            color: 'black',
            price: 450,
            imageFileName: 'IPhone_7.png',
            screen: '4,7 inch IPS',
            processor: 'A10 Fusion',
            ram: 2
        },

        {
            id: 1,
            name: 'iPhone 8',
            manufacturer: 'Apple',
            description: "lorem ipsum dolor sit amet consectetur.",
            color: "white",
            price: 650,
            imageFileName: "IPhone_8.png",
            screen: "4,7 inch IPS",
            processor: "A10 Fusion",
            ram: 2
        },

        {
            id: 2,
            name: "Samsung ",
            manufacturer: "Samsung",
            description: "lorem ipsum dolor sit amet consectetur.",
            color: "black",
            price: 750,
            imageFileName: "Samsung.png",
            screen: "4,7 inch IPS",
            processor: "A10 Fusion",
            ram: 2
        }

    ]

export const phone = () => {
    return (
        <div>
            <h2>Phone:</h2>
            {listPhone.map(phone => {
                return (
                    <div key={phone.id}>
                        <h3>
                            <Link to={`/phone/${phone.id}`}>{phone.name}</Link>
                        </h3>
                        <h4>{phone.manufacturer}</h4>
                        <h4>{phone.description}</h4>
                        <h4>{phone.color}</h4>
                        <h4>{phone.price}</h4>
                        <h4>{phone.screen}</h4>
                        <h4>{phone.processor}</h4>
                        <h4>{phone.ram}</h4>
                    </div>
                )
            })}

        </div>
    )
};
