import React, { Component } from 'react';
import axios from 'axios';

class AddPhone extends Component {
    state = {
        id: '',
        name: '',
        manufacturer: '',
        description: '',
        color: '',
        price: '',
        imageFileName: '',
        screen: '',
        processor: '',
        ram: ''
    }


handleFormSubmit = (event) => {
    event.preventDefault();
    const name = this.state.name;
    const manufacturer = this.state.manufacturer;
    const description = this.state.description;
    const color = this.state.color;
    const price = this.state.price;
    const imageFileName = this.state.imageFileName;
    const screen = this.state.screen;
    const processor = this.state.processor;
    const ram = this.state.ram;
    axios.post("http://localhost:5555/api/phones", {name, manufacturer, description, color, price, imageFileName, screen, processor, ram
    })
    .then (() => {
        this.setState ({name: "", manufacturer: "", description: "", color: "", price: "", imageFileName: "", screen: "", processor: "", ram: "" });
    })
    .catch (error => console.log(error))
}

handleChange = (event) => {
    const {name, manufacturer, description, color, price, imageFileName, screen, processor, ram} = event.target;
    this.setState ({[name]: value});
}


render () {
    return (
        <div>
            <form onSubmit = {this.handleFormSubmit}>
                <label>Name:</label>
                <input type="text" name="name" value={this.state.name} onChange= { e => this.handleChange (e) } />
                <label>Manufacturer:</label>
                <input type="text" name="manufacturer" value={this.state.manufacturer} onChange= { e => this.handleChange (e) } />
                <label>Description:</label>
                <input type="text" name="description" value={this.state.description} onChange= { e => this.handleChange (e) } />
                <label>Color:</label>
                <input type="text" name="color" value={this.state.color} onChange= { e => this.handleChange (e) } />
                <label>Price:</label>
                <input type="text" name="price" value={this.state.price} onChange= { e => this.handleChange (e) } />
                <label>ImageFileName:</label>
                <input type="text" name="imageFileName" value={this.state.imageFileName} onChange= { e => this.handleChange (e) } />
                <label>Screen:</label>
                <input type="text" name="screen" value={this.state.screen} onChange= { e => this.handleChange (e) } />
                <label>Processor:</label>
                <input type="text" name="processor" value={this.state.processor} onChange= { e => this.handleChange (e) } />
                <label>Ram:</label>
                <input type="text" name="ram" value={this.state.ram} onChange= { e => this.handleChange (e) } />

                <input type="submit" value="Submit" />
            </form>

        </div>
        )
    }
}

export default AddPhone;
