import React, {component} from 'react';
import { listPhone } from './Phone';
import {Link} from 'react-router-dom';

const phoneDetails = (props) => {

    console.log(props);

    const getPhone = (id) => {
        const thePhone = onePhone => {
            return onePhone.id === id;
        }
        return listPhone.find(thePhone)
    };

    const {params} = props.match;
    const foundPhone = getPhone(params.id, 10);



    return (
        <div>
            <h2>{foundPhone.name}</h2>
            <h3>manufacturer: {foundPhone.manufacturer}</h3>
            <h3>description: {foundPhone.description}</h3>
            <h3>color: {foundPhone.color}</h3>
            <h3>price: {foundPhone.price}</h3>
            <h3>screen: {foundPhone.screen}</h3>
            <h3>processor: {phone.processor}</h3>
            <h3>ram: {phone.ram}</h3>
            <Link to='/Phone'>Back to the list of Phones</Link>
        </div>
    )
}

export default phoneDetails;
