import React, {component} from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle,ListGroup, Button
  } from 'reactstrap';

class PhoneDetails extends React.Component {

    state = {
        phone: null
    }

    getPhoneData =  () => {
        const phoneName = this.props.match.params.id;
        axios.get(`/api/phones/${phoneName}`) 
        .then(response => {
            console.log(response.data);
            this.setState ({
               phone: response.data 
            })
        })
        .catch(err => console.log(err)); 
    }

    deletePhone = () => {
        axios.
            delete(`/api/phones/${this.state.phone._id}`)
            .then(() => {
                this.props.history.push("/phones");
            })
            .catch ((err) => {
                console.log(err);
            })
    };

    componentDidMount(){
       this.getPhoneData();
    }

    render () {

    if(!this.state.phone) return <h1>loading ...</h1>

    return(

        <div className="phoneDetails">

            <Card className="phoneCard" style={{ width: '32rem' }}>

                <CardBody>
                    <CardTitle className="cardTitle btnbtn-secondary btn-block my-4">{this.state.phone.name}</CardTitle>
                    <CardText>
                    {this.state.phone.description}
                    </CardText>
                </CardBody>

                <ListGroup className="list-group-flush">
                    <h1>{this.state.phone.manufacturer}</h1>
                    <h4>{this.state.phone.color}</h4>
                    <h4>{this.state.phone.color}</h4>
                    <h4>{this.state.phone.price}</h4>
                    <h4>{this.state.phone.screen}</h4>
                    <h4>{this.state.phone.processor}</h4>
                    <h4>{this.state.phone.ram}</h4>
                </ListGroup>
                <CardBody>
                    <Link to={'/projects'}>Back to projects</Link>
                    <button 
                        className="btn btn-info btn-block my-4"
                        onClick= {this.deletePhone} >
                        Delete Phone
                    </button>
                </CardBody>
            </Card>

        </div>
    )

    }

}

export default PhoneDetails;
