import React, {component} from 'react';
import axios from 'axios';
import { listPhone } from './Phone';
import {Link} from 'react-router-dom';

class PhoneDetails extends React.Component {

    state = {
        phone: null
    }

    getPhoneData =  () => {
        const phoneName = this.props.match.params.id;
        axios.get(`/api/phones/${phoneName}`) 
        .then(response => {
            console.log(response.data);
            this.setState ({
               phone: response.data 
            })
        })
        .catch(err => console.log(err)); 
    }

    deletePhone = () => {
        axios.
            delete(`/api/phones/${this.state.phone._id}`)
            .then(() => {
                this.props.history.push("/phones");
            })
            .catch ((err) => {
                console.log(err);
            })
    };

    componentDidMount(){
       this.getPhoneData();
    }

    render () {

    if(!this.state.phone) return <h1>loading ...</h1>

    return(

        <div className="phoneDetails">

        <Card style={{ width: '18rem' }}>

            <h1 className="">{this.state.phone.color}</h1>
            <h4>{this.state.phone.manufacturer}</h4>
            <h4>{this.state.phone.description}</h4>
            <h4>{this.state.phone.color}</h4>
            <h4>{this.state.phone.price}</h4>
            <h4>{this.state.phone.screen}</h4>
            <h4>{this.state.phone.processor}</h4>
            <h4>{this.state.phone.ram}</h4>

            <br/>
            <Link to={'/projects'}>Back to projects</Link>

            <button
                onClick= {this.deletePhone} >
                Delete Phone
            </button>
            </Card>

        </div>
    )

    }

}

export default PhoneDetails;
